mapper.update:
update `order` set ORDER_STATUS = #{processOrderStatus}, UPDATE_TIME = NOW()
WHERE ORDER_STATUS = #{currentOrderStatus} and ORDER_ID in

    #{orderId}

mapper.select:
select o.ORDER_ID as orderId, s.SHOP_NAME as shopName, o.PRICE as orderPrice, o.GOODS_COUNT as orderGoodsCount, oe.PROVINCE_CODE as provinceCode, oe.CITY_CODE as cityCode, oe.AREA_CODE as areaCode, oe.HAS_LOGISTICS as hasLogistics, oe.PROVINCE_NAME as provinceName, oe.CITY_NAME as cityName, oe.AREA_NAME as areaName, oe.ADDRESS as address, oe.REMARK as remark, o.CREATE_TIME as createTime, o.UPDATE_TIME as updateTime, o.ORDER_STATUS as orderStatus from `order` o join shop s on s.SHOP_ID = o.SHOP_ID join order_ext oe on oe.ORDER_ID = o.ORDER_ID where o.SHOP_ID = #{shopId}
and o.ORDER_STATUS = #{item.orderStatus}
order by o.CREATE_TIME desc
